import { useEffect, useState } from "react"
import api from "../../utils/api";
import { useNavigate } from 'react-router-dom';
import mydata from "./card-data.json";
import { useReducer } from 'react';
const reducer = (state, action) => {
    switch (action.type) {
      case "INCREASE":
        return state.map((player) => {
          if (player.id === action.id) {
            return { ...player, score: player.score + 1 };
          } else {
            return player;
          }
        });
        case "DECREASE":
        return state.map((player) => {
          if (player.id === action.id) {
            return { ...player, score: player.score -1 };
          } else {
            return player;
          }
        });
      default:
        return state;
    }
  };
export default function Dashbord() {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        const fetchdata = async () => {
            try {
                setLoading(true);
                const response = await api.get('/user/dashboard');
                if (!response.data.success) {
                    navigate('/');
                }
                else {
                    setLoading(false);
                }
            } catch (error) {
                // console.error("Error fetching data:", error);
                navigate('/');
            }
        }
        fetchdata();

    }, [])



      const [state, dispatch] = useReducer(reducer, {score:0});

  const handleIncrease = (player) => {
    dispatch({ type: "INCREASE", id: player.id });
  };
  const handleDecrease = (player) => {
    dispatch({ type: "DECREASE", id: player.id });
  };
    if (loading) return <div className=" d-flex justify-content-center text-center app-loader" >
        <div className="loader">
        </div>
    </div>;
    return (
        <>
            {mydata.map(mydatas =>
                <div className="container-fluid mt-5">
                    {/* <div className="row"> */}
                    <div className="col-sm-3">
                        <div className="card">
                            <li className="disabled" key={mydatas.id}>...</li>
                            <div className="text-center">
                                <img src={mydatas.image} alt="..." className="imagesizeview card-img-top" />
                            </div>
                            <div className="card-body">
                                <h5 className="card-title">{mydatas.name}</h5>
                                {/* <p className="card-text text-center">status for luxury</p> */}
                                {/* <div className="container"> */}
                                <div className="row">
                                    <div className="col">
                                        Prize
                                    </div>
                                    <div className="col">
                                        {mydatas.price}
                                    </div>
                                </div>
                                <div className="row mt-2">
                                    <div className="col">
                                        Quantity
                                    </div>
                                    <div className="col">
                                        <div className="btn-group" role="group" aria-label="Basic example">
                                            <a type="button" className="btn btn-outline-success"onClick= {() => handleDecrease(mydatas)}><b>-</b></a>
                                            <a className="btn btn-outline-success " style={{ pointerEvents: 'none' }}> {mydatas.score}</a>
                                            <a type="button" className="btn btn-outline-success" onClick= {() => handleIncrease(mydatas)}><b>+</b></a>
                                        </div>
                                    </div>
                                </div>
                                {/* </div> */}
                                {/* <a href="/" className="btn btn-primary">Go somewhere</a> */}
                            </div>

                        </div>
                    </div>
                    {/* </div> */}
                </div>
            )}
        </>
    )
}
